<html>
    <head>
        <title>JS API Test</title>
        <script src="http://ajax.googleapis.com/ajax/libs/jquery/1.7/jquery.min.js" type="text/javascript"></script>
        <script src="https://api.cert.nabcommerce.com/1.0/velocity.js" type="text/javascript"></script>

        <script type="text/javascript">
            $(document).ready(function() {
            
                $("#process-payment-btn").click(function(){
					var applicationprofileid = 14644;
                    var merchantprofileid = "PrestaShop Global HC";
					var workflowid = 2317000001;
                    var sessionToken = '<?php echo $sessionToken; ?>';
                     
                    var  card = {
					        CardholderName: $("#card_holder_name").val(), cardtype: $("#cardtype").val(), number: $("#pan").val(), 
							cvc: $("#cvc").val(), expMonth: $("#exp-mo").val(), expYear: $("#exp-year").val()
                    };
                                 
                    var  address = {
                            Street: $("#street").val(),
							PostalCode: $("#zip").val()
                    };

                    Velocity.tokenizeForm(sessionToken, card, address, applicationprofileid, merchantprofileid, workflowid, responseHandler);
                    return false;
                });
              
                function responseHandler(result) {
                    // Display full result for debugging.
                    $('#result').html("Error:<br />");

                    if (result['code'] == 0) {
                        // Request was successful. Insert hidden field into the form before submitting.
                        $('#payment').append("<input type='hidden' name='TransactionToken' value='"+result.text+"' />");
                        // Continue to submit the form to the action, where we will read the decode and extract POST data.
                        document.forms['payment'].submit();
                    }else {
					
                        for (var i in result) {
							$('#result').append(i+": "+result[i]+"<br />");
                        }
						
                    }
                }
            });
        </script>
     
